nasal.load:
    loop_id = 0;

    setprop("/models/static/rjbb_wingshuttle4_x", 700.0);
    setprop("/models/static/rjbb_wingshuttle4_richt", 'rueck');

    train_pos = func
               {
               x = getprop("/models/static/rjbb_wingshuttle4_x");
               if (getprop("/models/static/rjbb_wingshuttle4_richt") == 'vor')
                 {
                 x = x + 1;
                 }
                 else
                 {
                 x = x - 1;
                 }
               if (x > 1500)
                 {
                 x = 1500.0;
                 setprop("/models/static/rjbb_wingshuttle4_richt", 'rueck');
                 }
               if (1 > x)
                 {
                 x = 1.0;
                 setprop("/models/static/rjbb_wingshuttle4_richt", 'vor');
                 }
              
               setprop("/models/static/rjbb_wingshuttle4_x", x);
              
               }

    loop = func(id) 
           {
           train_pos();
           id == loop_id or return;
           settimer(func { loop(id) }, 0.05);
           }

    loop(loop_id += 1);
